package cafe24practice;

public class study7 {
	
	public static String numrer(int i){
		if(i==0){
			return "zero";
		} else if(i==1){
			return "one";
		} else if(i==2){
			return "two";
		}
		return "none";
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		numrer(0);
	}

}
